import React, {Component} from 'react';
import Header from '../components/Header.js';



class App extends Component {
    constructor(props) {
        super(props)
        this.state = {
            longitude: undefined,
            latitude: undefined,
            // locationNow: (async function() {
            //     const locNow = await getLocation()
            //     // console.log(locNow)
            //     this.setState.longitude= locNow[0];
            //     this.setState.latitude= locNow[1];
            // })(),
        }
    }

    // const coords = {
    //         longitude: undefined,
    //         latitude: undefined,
    //         locationNow: (async function() {
    //             const locNow = await getLocation()
    //             // console.log(locNow)
    //             this.longitude= locNow[0];
    //             this.latitude= locNow[1];
    //         })(),
    //     }


    componentDidMount() {

        function getLocation() {
            let loc = [];
                fetch('http://api.open-notify.org/iss-now.json')
                .then (Response => {return Response.json()})
                .then (function(response) {
                    // console.log(response)
                    // const iss = []
                    // iss.push(response);
                    // console.log(iss[0])
                    // return iss[0]
                    loc.push(response.iss_position.longitude);
                    loc.push(response.iss_position.latitude);
                })
                return loc
            }


        let coords = getLocation()
        this.setState({
            longitude: coords[0],
            latitude: coords[1],
        })
    }



    render() {


    return (

        <div>
            <div>
                <Header />
            </div>
            <div>
                <p>The current location of the ISS is longitude: {this.state.longitude}, latitude {this.state.latitude}</p>
                <button>Click here to know!</button>
            </div>
        </div>
    )
    }
}

export default App;
