import React from 'react';


async function getLocation() {
    let loc = []
        await fetch('http://api.open-notify.org/iss-now.json')
        .then (Response => {return Response.json()})
        .then (function(response) {
            // console.log(response)
            // const iss = []
            // iss.push(response);
            // console.log(iss[0])
            // return iss[0]
            loc.push(response.iss_position.longitude);
            loc.push(response.iss_position.latitude);
        })
        return loc
    }


const App = () => {

    const coords = {
        longitude: undefined,
        latitude: undefined,
        locationNow: (async function() {
            debugger;
            const locNow = await getLocation()
            // console.log(locNow)
            this.longitude= locNow[0];
            this.latitude= locNow[1];
        })(),
    }

    return (

        <div>
            <div>
                <p>The current location of the ISS is longitude: {coords.longitude}, latitude {coords.latitude}</p>     
            </div>
            <button>Click here to know!</button>
        </div>
    )
}

export default App;
